  .more-button {
    position: relative;
    margin-top: 24px;
    margin-bottom: 100px;
    width: 300px;
    height: 43px;
    line-height: 43px;
    border: 1px solid $main-color;
    border-radius: 50px;

    &:hover {
      color: $sub-color;
      background-color: $main-color; 
    }
